import React,{Component} from 'react';
import {BrowserRouter as Router} from "react-router-dom";
import { BodyHomeBody, PageBody, Titre2, Carte,ImageLogo,SousTitre,BodyBordure,Titre2Blanc,SousTitreBlanc,BodyBouton} from './BodyHome';
import {json,select} from 'd3';
import logo from '../../images/LogoNatan.jpg';

function getJourNom(Date) {
    switch (Date) {
        case 0:
            return "Dimanche";
        case 1:
            return "Lundi";
        case 2:
            return "Mardi";
        case 3:
            return "Mercredi";
        case 4:
            return "Jeudi";
        case 5:
            return "Vendredi";
        case 6:
            return "Samedi";
        default: return Date;
    }
}
function getJourEmplacement(Jour,AdresseEmplacement) {
    json("/data/Emplacement.json").then((value) => {
        if(Jour !== 1 && Jour !== 2){
            if(Jour === 0)return value[0].EmplacementCamion.Adresse;
            else return value[Jour-2].EmplacementCamion.Adresse;
        }
    })
    if(Jour === 1 ||Jour === 2)return "Pas de Pizzas ce jour la";
    console.log(Jour);
}
class BodyHome extends Component {
    state = {
        DisponibleTexte: null,
        Endroit: null,
        Jour:null,
        JourEmplacement: new Date().getDay(),
        AdresseEmplacement: null
    }
    setJourEmplacement(Param) {
        if(Param === -1 && this.state.JourEmplacement === 0)this.setState({JourEmplacement:6});
        else {
            if(Param === 1 && this.state.JourEmplacement === 6)this.setState({JourEmplacement:0});
            else this.setState({JourEmplacement:this.state.JourEmplacement+Param});
        }
    }
    
    componentDidMount() {
        
        json("/data/Emplacement.json").then((value) => {
            var DisponibleTexte, Jour, Endroit;
            Jour = new Date().getDay();
            this.setState({ Jour: Jour });
            this.setState({ JourEmplacement: Jour });
            if(this.state.JourEmplacement !== 1 && this.state.JourEmplacement !== 2){
                this.setState({ AdresseEmplacement: value[this.state.JourEmplacement-2].EmplacementCamion.Adresse });
            }
            if (Jour > 2 || Jour === 0) {
                if (Jour === 0) {
                    Endroit = " et nous vous attendons à " + value[4].EmplacementCamion.Ville;
                    this.setState({ Endroit: Endroit });
                }
                else {
                    Endroit = " et nous vous attendons à " + value[Jour-2].EmplacementCamion.Ville;
                    this.setState({ Endroit: Endroit });
                }
                DisponibleTexte = "Aujourd'hui nous faisons des Pizzas !";
                this.setState({ DisponibleTexte: DisponibleTexte });
            } else {
                DisponibleTexte = "Aujourd'hui nous ne faisons pas de Pizza, mais regardez notre carte";
                Endroit = " ";
                this.setState({ DisponibleTexte: DisponibleTexte });
                this.setState({ Endroit: Endroit });
                var e = document.getElementById('map');
                e.style.width = '0';
                e.style.height = '0';
                e.style.margin = '0';
            }
        });
    }
    render() {
        return (
            
                <Router forceRefresh={true}>
                    <BodyHomeBody>
                        <PageBody>
                            <ImageLogo>
                                <img src={logo}/>
                            </ImageLogo>
                            <Titre2 id="texteDispo">{this.state.DisponibleTexte}</Titre2>
                            <Carte id="map" />
                            <SousTitre>Nous sommes {getJourNom(this.state.Jour)}{this.state.Endroit}</SousTitre>
                            <BodyBordure>
                                <Titre2Blanc>Voici où nous nous situons en fonction du jour</Titre2Blanc>
                                
                                <SousTitreBlanc>
                                    <BodyBouton onClick={this.setJourEmplacement.bind(this,-1,this.state.AdresseEmplacement)}>Jour prédédent</BodyBouton>
                                        {getJourNom(this.state.JourEmplacement)}
                                        {getJourEmplacement(this.state.JourEmplacement)}
                                    <BodyBouton onClick={this.setJourEmplacement.bind(this,+1,this.state.AdresseEmplacement)}>Jour suivant</BodyBouton>
                                </SousTitreBlanc>
                                <SousTitreBlanc></SousTitreBlanc>
                            </BodyBordure>
                    </PageBody>
                    </BodyHomeBody>
                </Router>
            
        );
    }
}

export default BodyHome;
