import React from 'react';
import {BrowserRouter as Router,Link} from "react-router-dom";
import { BodyHomeBody, PageBody, Titre2, Carte} from './BodyHome';
import {json} from 'd3';

function getJour() {
    const date = new Date();
    const Jour = date.getDay();
    return Jour;
}

const BodyHome = () => {
    var DisponibleTexte,Jour,Endroit;
    var Jour = getJour();
    if(Jour == 0 || Jour >2){
        DisponibleTexte = "Aujourd'hui nous faisons des Pizzas !";
        const data =json("../../../data/Emplacement.json");
        Endroit = "et nous vous attendons à {Endroit}"+data[Jour-3].Jour;
    } 
    else DisponibleTexte = "Aujourd'hui nous ne faisons pas de Pizza, mais regardez notre carte";
    
    return (
        <>
            <Router forceRefresh={true}>
                <BodyHomeBody>
                    <PageBody>
                        <Titre2>{DisponibleTexte}</Titre2>
                    </PageBody>
                        <Titre2>Nous sommes {Jour}" "{Endroit}</Titre2>
                    <Carte id = "map"/>
                </BodyHomeBody>
            </Router>
        </>
    );
}

export default BodyHome;
