import React from 'react';
import {BrowserRouter as Router,Link} from "react-router-dom";
import { BodyHomeBody, PageBody, Titre2, Carte} from './BodyHome';
import {json} from 'd3';

function getDisponible() {
    const date = new Date();
    const Jour = date.getDay();
    if(Jour != 1 && Jour != 2)return true;
    else return false;
}

const BodyHome = () => {
    var DisponibleTexte;
    const Disponible = getDisponible();
    if(Disponible == true) DisponibleTexte = "Aujourd'hui nous faisons des Pizzas !";
    else DisponibleTexte = "Aujourd'hui nous ne faisons pas de Pizza, mais regardez notre carte";
    d3.json("../../../data/Emplacement.json", function(data) {
        X = data[Jour-3].EmplacementCamion.X;
        Y = data[Jour-3].EmplacementCamion.Y;
    });
    return (
        <>
            <Router forceRefresh={true}>
                <BodyHomeBody>
                    <PageBody>
                        <Titre2>{DisponibleTexte}</Titre2>
                    </PageBody>
                        <Titre2>Nous sommes {DisponibleTexte}</Titre2>
                    <Carte id = "map"/>
                </BodyHomeBody>
            </Router>
        </>
    );
}

export default BodyHome;
