import React, { Component } from 'react';
import { BrowserRouter as Router, Link } from "react-router-dom";
import { BodyHomeBody, PageBody, Titre2, FlexContainer, FlexItem, FlexItemB, FlexItemP } from './ListePizzas';
import { json, select } from 'd3';
class ListePizzas extends Component {

    state = {
        Pret:false,
        PizzasBaseCreme: []
    }
    componentDidMount() {
        json("/data/Pizzas.json").then((value) => {
            var copie = [];
            value.forEach(function (element) {
                var copie2 = [];
                element.Ingrédients.forEach(function (element2) {
                    copie2.push({ Ingrédient: element2.Ingrédient });
                });
                copie.push({ Nom: element.Nom, Ingrédients: copie2 });
            });
            //this.setState({ Pret : true});
            this.setState({ PizzasBaseCreme: copie});
            console.log(this.state.PizzasBaseCreme[0])
        });
        
    }
    render() {
        //console.log(this.state.PizzasBaseCreme[4].Ingrédients[0].Ingrédient);
        return (
            <Router forceRefresh={true}>
                <BodyHomeBody>
                    <PageBody>
                        <Titre2>Une vue des différentes Pizzas disponibles</Titre2>
                        <FlexContainer>
                            {this.state.PizzasBaseCreme.map(Pizzas => (<FlexItem><FlexItemP>{Pizzas.Nom}
                                {Pizzas.Ingrédients.map(Ingred => (Ingred.Ingrédient))}
                            </FlexItemP></FlexItem>))}
                        </FlexContainer>
                    </PageBody>
                </BodyHomeBody>
            </Router>
        );
    }

}

export default ListePizzas;
