import React, { Component } from 'react';
import myData from '../../images/Secret.json';
import { json } from 'd3';
import { BodyHomeBody, PageBody, Titre2,Form } from './Secret';
class Secret extends Component {
    constructor(props) {
        super(props);

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }
    state = {
        value: "Mot de passe"
    }
    handleChange(event) {
        this.setState({ value: event.target.value });
    }
    handleSubmit(event) {
        var Valeur = this.state.value;
        var fs = require('browserify-fs');
        var content;
        var jsonT, obj,test;
        fs.readFile(myData, 'utf8', function (err, data) {
            test = JSON.parse(data);
            content = test;
        });
        if (Valeur === JSON.stringify(content.table[0].Nombre) && content.vu === 0) {
            alert('Bien joué voici votre code pour votre Pizza');
            obj = {
                vu: content.vu + 1,
                Nombre: 0,
                table: []
            };
            obj.table.push({ Nombre: (Math.floor(Math.random() * 10000)) });
            jsonT = JSON.stringify(obj);
            console.log(jsonT);
            fs.writeFile(myData, jsonT, function () {
                fs.readFile(myData, 'utf-8', function (err, data) {
                    console.log(data);
                });
            }
            );
        } else {
            if ("new" === Valeur) {
                obj = {
                    vu: 0,
                    Nombre: 0,
                    table: []
                };
                obj.table.push({ Nombre: (Math.floor(Math.random() * 10000)) });
                jsonT = JSON.stringify(obj);
                console.log(jsonT);
                fs.writeFile(myData, jsonT, function () {
                    fs.readFile(myData, 'utf-8', function (err, data) {
                        console.log(data);
                    });
                }
                );
            } else {

                obj = {
                    vu: 0,
                    Nombre: content.Nombre + 1,
                    table: []
                };
                obj.table.push({ Nombre: content.table[0].Nombre });
                jsonT = JSON.stringify(obj);
                console.log(jsonT);
                fs.writeFile(myData, jsonT, function () {
                    fs.readFile(myData, 'utf-8', function (err, data) {
                        console.log(data);
                    });
                }
                );

            }
            fs.readFile(myData, 'utf8', function (err, data) {
                test = JSON.parse(data);
                content = test;
            });
            alert('Déjà ' + content.Nombre + ' personnes ont essayé mais sans succès');
        }



        event.preventDefault();
    }

    componentDidMount() {
        
    }
    render() {
        return (
                <BodyHomeBody>
                    <PageBody>
                        <Titre2>Vous venez de découvrir l'existence d'un secret... Mais avez-vous bien regardé tout le site ?</Titre2>
                        <Form onSubmit={this.handleSubmit}>
                            <label>
                                Donné le mot de passe dissimulé pour débloquer quelque chose :
                                    <textarea value={this.state.value} onChange={this.handleChange} />
                            </label>
                            <input type="submit" value="Envoyer" />
                        </Form>
                    </PageBody>
                </BodyHomeBody>
        );
    }
}

export default Secret;
