import React, { Component } from 'react';
import { BodyHomeBody, PageBody, Titre2,Form } from './Secret';
class Secret extends Component {
    constructor(props) {
        super(props);
    
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }
    state = {
        value:["Aucun élément choisi"]
    }
    handleChange(event) {
        var copie = [];
        if(event.target.value === "viande"){
            copie.push("viande");
            copie.push("Jambon");
            copie.push("Lardons");
            copie.push("Poulet");
            copie.push("Poulet Mariné au citron");
            copie.push("Kebab");
            copie.push("Chorizo");
            copie.push("Viande hachée");
        }else{
            copie.push(event.target.value);
        }
        this.setState({ value: copie });
    }

    handleSubmit(event) {
        alert('Votre demande a été soumise');
        event.preventDefault();
        console.log(this.state.value);
        this.ajoutListePizza();
    }

    componentDidMount() {
        
    }
    render() {
        return (
                <BodyHomeBody>
                    <PageBody>
                        <Titre2>Vous venez de découvrir l'existence d'un secret... Mais avez-vous bien regardé tout le site ?</Titre2>
                        <Form onSubmit={this.handleSubmit}>
                            <label>
                                Donné le mot de passe dissimulé pour débloquer quelque chose :
                                <select value={this.state.value[0]}  onChange={this.handleChange}>
                                    <option selected value="Aucun élément choisi">Aucun</option>
                                    <option value="viande">Viande</option>
                                    <option value="Poivrons confits">Poivrons</option>
                                </select>
                            </label>
                            <input type="submit" value="Envoyer" />
                        </Form>
                    </PageBody>
                </BodyHomeBody>
        );
    }
}

export default Secret;
