import React from 'react';
import {BrowserRouter as Router} from "react-router-dom";
import { BodyHomeBody, PageBody, Titre2, Carte} from './BodyHome';
import {json} from 'd3';

function getJourNom() {
    switch (new Date().getDay()) {
        case 0:
          return "Dimanche";
        case 1:
          return "Lundi";
        case 2:
           return "Mardi";
        case 3:
            return "Mercredi";
        case 4:
            return "Jeudi";
        case 5:
            return "Vendredi";
        case 6:
            return "Samedi";
        default : return "bug";
      }
}

const BodyHome = () => {
    var DisponibleTexte,Jour,Endroit;
    //var Jour = new Date().getDay();
    Jour = 3;

    var data = json("/data/Emplacement.json");
    console.log(data[3].EmplacementCamion.X);
        console.log(data[3].EmplacementCamion.Y);
        console.log("ta mere");
    if(Jour >2 || Jour === 0){
        if(Jour === 0){
            Endroit = " et nous vous attendons à "/*+data[4].EmplacementCamion.Ville*/;
        }
        else{
            Endroit = " et nous vous attendons à "/*+data[Jour-1].EmplacementCamion.Ville*/;
        }
        DisponibleTexte = "Aujourd'hui nous faisons des Pizzas !";
        
    }else{
        DisponibleTexte = "Aujourd'hui nous ne faisons pas de Pizza, mais regardez notre carte";
        Endroit = " ";
    } 
    
    return (
        <>
            <Router forceRefresh={true}>
                <BodyHomeBody>
                    <PageBody>
                        <Titre2>{DisponibleTexte}</Titre2>
                        <Titre2>Nous sommes {getJourNom()}{Endroit}</Titre2>
                        <Carte id = "map"/>
                        Test
                    </PageBody>
                </BodyHomeBody>
            </Router>
        </>
    );
}

export default BodyHome;
