import React from 'react';
import {BrowserRouter as Router,Link, Switch} from "react-router-dom";
import { BodyHomeBody, PageBody, Titre2, Carte} from './BodyHome';
import {json} from 'd3';

function getJour() {
    const date = new Date();
    const Jour = date.getDay();
    return Jour;
}
function getJourNom() {
    switch (new Date().getDay()) {
        case 0:
          return "Sunday";
          break;
        case 1:
          return "Monday";
          break;
        case 2:
           return "Tuesday";
          break;
        case 3:
            return "Wednesday";
          break;
        case 4:
            return "Thursday";
          break;
        case 5:
            return "Friday";
          break;
        case 6:
            return "Saturday";
      }
}

const BodyHome = () => {
    var DisponibleTexte,Jour,Endroit,JourNom;
    var Jour = getJour();
    const data =json("../../../data/Emplacement.json");
    if(Jour == 0 || Jour >2){
        DisponibleTexte = "Aujourd'hui nous faisons des Pizzas !";
        Endroit = " et nous vous attendons à "+data[Jour-3].EmplacementCamion.Ville;
        JourNom = data[Jour-3].Jour;
    } 
    else{
        DisponibleTexte = "Aujourd'hui nous ne faisons pas de Pizza, mais regardez notre carte";
        Endroit = " ";
        JourNom = data[Jour-3].Jour;
    } 
    
    return (
        <>
            <Router forceRefresh={true}>
                <BodyHomeBody>
                    <PageBody>
                        <Titre2>{DisponibleTexte}</Titre2>
                    </PageBody>
                        <Titre2>Nous sommes {JourNom}{Endroit}</Titre2>
                    <Carte id = "map"/>
                </BodyHomeBody>
            </Router>
        </>
    );
}

export default BodyHome;
