import React from 'react';
import {BrowserRouter as Router,Link} from "react-router-dom";
import { BodyHomeBody, PageBody, Titre2} from './BodyHome';

function getDisponible() {
    const date = new Date();
    const Jour = date.getDay();
    if(Jour <0 && Jour > 3)return true;
    else return false;
}

const BodyHome = () => {
    /*const Emplacement =json('../../../src/data/Emplacement.json');*/
    const DisponibleTexte = "";
    if(getDisponible() == true) DisponibleTexte = "Aujourd'hui nous faisons des Pizzas !";
    else DisponibleTexte = "Aujourd'hui nous ne faisons pas de Pizza, mais regardez notre carte";
    return (
        <>
            <Router forceRefresh={true}>
                <BodyHomeBody>
                    <PageBody>
                        <Titre2>{DisponibleTexte}</Titre2>
                        
                    </PageBody>
                </BodyHomeBody>
            </Router>
        </>
    );
}

export default BodyHome;
