import React, { Component } from 'react';
import myData from '../../images/Secret.json';
import { json } from 'd3';
import { BodyHomeBody, PageBody, Titre2,Form } from './Secret';
class Secret extends Component {
    constructor(props) {
        super(props);
    
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }
    state = {
        value:"Mot de passe"
    }
    handleChange(event) {
        this.setState({value: event.target.value});
    }

    handleSubmit(event) {
        var Valeur = this.state.value;
        var fs = require('browserify-fs');
        var content;
        fs.readFile(myData, 'utf8', function(err, data) {
            var test = JSON.parse(data);
            content = test;
            console.log(content.table[0].Nombre);
            if(this.state.value === content.table[0].Nombre){
                alert('Bien joué voici votre Pizza');
            }else{
                alert('Cherchez vraiment');
            }
          });
            /*if(Valeur !== content.table[0].Nombre){
                var obj = {
                    table: []
                };
                obj.table.push({Nombre: (Math.random() * (10000 - 0) + 0)});
                var jsonT = JSON.stringify(obj);
                console.log(jsonT);
                fs.writeFile(myData, jsonT, function() {
                    fs.readFile(myData, 'utf-8', function(err, data) {
                        console.log(data);
                    });}
                );
            }*/

       
        if(this.state.value === content.table[0].Nombre){
            alert('Bien joué voici votre Pizza');
        }else{
            alert('Cherchez vraiment');
        }
        event.preventDefault();
        console.log(this.state.value);
    }

    componentDidMount() {
        
    }
    render() {
        return (
                <BodyHomeBody>
                    <PageBody>
                        <Titre2>Vous venez de découvrir l'existence d'un secret... Mais avez-vous bien regardé tout le site ?</Titre2>
                        <Form onSubmit={this.handleSubmit}>
                            <label>
                                Donné le mot de passe dissimulé pour débloquer quelque chose :
                                    <textarea value={this.state.value} onChange={this.handleChange} />
                            </label>
                            <input type="submit" value="Envoyer" />
                        </Form>
                    </PageBody>
                </BodyHomeBody>
        );
    }
}

export default Secret;
