    constructor(props) {
        super(props);
    
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
      }
    state = {
        value:"Mot de passe"
    }
    handleChange(event) {
        this.setState({value: event.target.value});
    }

    handleSubmit(event) {
        if(new Date.getMonth())
        if(this.state.value === "m"){
            alert('Bien joué voici votre Pizza');
        }else{
            alert('Cherchez vraiment');
        }
        event.preventDefault();
        console.log(this.state.value);
    }

    componentDidMount() {
        
    }
    render() {
        return (
                <BodyHomeBody>
                    <PageBody>
                        <Titre2>Vous venez de découvrir l'existence d'un secret... Mais avez-vous bien regardé tout le site ?</Titre2>
                        <Form onSubmit={this.handleSubmit}>
                            <label>
                                Donné le mot de passe dissimulé pour débloquer quelque chose :
                                    <textarea value={this.state.value} onChange={this.handleChange} />
                            </label>
                            <input type="submit" value="Envoyer" />
                        </Form>
                    </PageBody>
                </BodyHomeBody>
        );
    }
}

export default Secret;
