import React from 'react';
import {BrowserRouter as Router} from "react-router-dom";
import { BodyHomeBody, PageBody, Titre2, Carte} from './BodyHome';
import {json} from 'd3';

function getJourNom() {
    switch (new Date().getDay()) {
        case 0:
          return "Dimanche";
        case 1:
          return "Lundi";
        case 2:
           return "Mardi";
        case 3:
            return "Mercredi";
        case 4:
            return "Jeudi";
        case 5:
            return "Vendredi";
        case 6:
            return "Samedi";
        default : return "bug du nombre de jour";
      }
}
function setBody(Endroit,DisponibleTexte){
    BodyHome = () =>{
        return (
            <>
                <Router forceRefresh={true}>
                    <BodyHomeBody>
                        <PageBody>
                            <Titre2>{DisponibleTexte}</Titre2>
                            <Titre2>Nous sommes {getJourNom()}{Endroit}</Titre2>
                            <Carte id = "map"/>
                            Test
                        </PageBody>
                    </BodyHomeBody>
                </Router>
            </>
        );
    }
}

var BodyHome = () => {
    var DisponibleTexte,Jour,Endroit;
    Jour = new Date().getDay()+1;
    var data = json("/data/Emplacement.json");
        
    data.then((value) => {
        if(Jour >2 || Jour === 0){
            if(Jour === 0){
                Endroit = " et nous vous attendons à "+value[0].EmplacementCamion.Ville;
            }
            else{
                Endroit = " et nous vous attendons à "+value[Jour-2].EmplacementCamion.Ville;
            }
            DisponibleTexte = "Aujourd'hui nous faisons des Pizzas !";
            console.log(Endroit);
            setBody(Endroit,DisponibleTexte);
        }else{
            DisponibleTexte = "Aujourd'hui nous ne faisons pas de Pizza, mais regardez notre carte";
            Endroit = " ";
            setBody(Endroit,DisponibleTexte);
        } 
    });
    
    //while(Endroit === undefined){}
    console.log(Endroit);
    return (
        <>
            <Router forceRefresh={true}>
                <BodyHomeBody>
                    <PageBody>
                        <Titre2>{DisponibleTexte}</Titre2>
                        <Titre2>Nous sommes {getJourNom()}{Endroit}</Titre2>
                        <Carte id = "map"/>
                        Test
                    </PageBody>
                </BodyHomeBody>
            </Router>
        </>
    );
}

export default BodyHome;
