import React from 'react';
import {BrowserRouter as Router,Link, Switch} from "react-router-dom";
import { BodyHomeBody, PageBody, Titre2, Carte} from './BodyHome';
import {json} from 'd3';

function getJourNom() {
    switch (new Date().getDay()-1) {
        case 0:
          return "Dimanche";
          break;
        case 1:
          return "Lundi";
          break;
        case 2:
           return "Mardi";
          break;
        case 3:
            return "Mercredi";
          break;
        case 4:
            return "Jeudi";
          break;
        case 5:
            return "Vendredi";
          break;
        case 6:
            return "Samedi";
      }
}

const BodyHome = () => {
    var DisponibleTexte,Jour,Endroit;
    var Jour = (new Date().getDay())+1;
    const data =json("../../../data/Emplacement.json");
    if(Jour >2 || Jour == 0){
        if(Jour == 0)Endroit = " et nous vous attendons à "+data[4].EmplacementCamion.Ville;
        else Endroit = " et nous vous attendons à "+data[Jour-1].EmplacementCamion.Ville;
        DisponibleTexte = "Aujourd'hui nous faisons des Pizzas !";
        
    }else{
        DisponibleTexte = "Aujourd'hui nous ne faisons pas de Pizza, mais regardez notre carte";
        Endroit = " ";
    } 
    
    return (
        <>
            <Router forceRefresh={true}>
                <BodyHomeBody>
                    <PageBody>
                        <Titre2>{DisponibleTexte}</Titre2>
                        <Titre2>Nous sommes {getJourNom()}{Endroit}</Titre2>
                        <Carte id = "map"/>
                        Test
                    </PageBody>
                </BodyHomeBody>
            </Router>
        </>
    );
}

export default BodyHome;
