import React, { Component } from 'react';
import { BrowserRouter as Router, Link } from "react-router-dom";
import { BodyHomeBody, PageBody, Titre2, FlexContainer, FlexItem, FlexItemB, FlexItemP } from './ListePizzas';
import { json, select } from 'd3';
class ListePizzas extends Component {

    state = {
        PizzasBaseCreme: [],
        Ingrédients: []
    }
    componentDidMount() {
        json("/data/Pizzas.json").then((value) => {
            var copie = [];
            value.forEach(function (element) {
                var copie2 = [];
                copie.push({ Nom: element.Nom });
                element.Ingrédients.forEach(function (element2) {
                    copie2.push({ Ingrédient: element2.Ingrédient });
                });
                console.log(copie2);
                copie.push({ Nom: element.Nom, Ingrédients: copie2 });
            });
            this.state.PizzasBaseCreme = copie;
            //value.forEach(element => this.state.PizzasBaseCreme.push({Nom : element.Nom}));
            //value.forEach(element => element.Ingrédients.forEach(data => this.state.Ingrédients.push({Ingrédient : data.Ingrédient})));
            this.setState({ Nom: this.state.PizzasBaseCreme[0].Nom });
            this.setState({ Ingrédients: this.state.PizzasBaseCreme[0].Ingrédients });
            //this.setState({ Ingredients : this.state.Ingrédients[0]});
            console.log(this.state.PizzasBaseCreme[0])
        });
    }
    render() {
        console.log(this.state.PizzasBaseCreme[4]);
        return (
            <Router forceRefresh={true}>
                <BodyHomeBody>
                    <PageBody>
                        <Titre2>Une vue des différentes Pizzas disponibles</Titre2>
                        <FlexContainer>
                            {this.state.PizzasBaseCreme.map(Pizzas => (<FlexItem><FlexItemP>{Pizzas.Nom}</FlexItemP></FlexItem>))}
                        </FlexContainer>
                    </PageBody>
                </BodyHomeBody>
            </Router>
        );
    }

}

export default ListePizzas;
